package au.com.helixta.app;

import au.com.helixta.list.LinkedList;

import static au.com.helixta.utilities.StringUtils.join;
import static au.com.helixta.utilities.StringUtils.split;
import static au.com.helixta.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
